service-task-definition.script:
main();
function main(){
  //// == Config Retrieving / 工程コンフィグの参照 ==
  const oauth2 = configs.get( "OAuth2" ) + "";
  const fileId = configs.get( "SourceFileId" ) + "";
  const folderId = configs.get( "FolderId" ) + "";
  const saveIdData = configs.get( "DataForId" ) + "";
  const saveUrlData = configs.get( "DataForUrl" ) + "";
  const newFileName = configs.get( "NewFileName" ) + "";

  if(fileId === "" || fileId === null){
    throw "No Source File ID";
  }

  const reg = new RegExp('[/\]');
  if(newFileName.length>255){
    throw"File Name shoule be less than 256 characters";
  }else if(newFileName.search(reg) !== -1|| newFileName.slice(-1) == " " || newFileName == "." || newFileName == ".."){
    throw "Invalid File Name";
  }

  const token  = httpClient.getOAuth2Token( oauth2 );
  const json2 = getFolderID(token, fileId);
  const json = copyFile(token, folderId, fileId, newFileName);

  if (saveIdData !== null && saveIdData !== "") {
      engine.setDataByNumber(saveIdData, json["id"]);
  }
  if (saveUrlData !== null && saveUrlData !== "") {
      engine.setDataByNumber(saveUrlData, "https://app.box.com/file/" + json["id"]);
  }

  //get parent folderID of file
function getFolderID(token, fileId){

  let url2 = "https://api.box.com/2.0/files/";
    url2 += fileId;

    const response2 = httpClient.begin()
      .bearer(token)
      .get(url2);


    const responseTxt2 = response2.getResponseAsString();
    let jsonRes2 = JSON.parse(responseTxt2);

    engine.log(responseTxt2);
    return jsonRes2;


}

  //copy file on Box
function copyFile(token, folderId, fileId, newFileName) {
  let jsonReq = {};

  if (folderId !== "" && folderId !== null){
    jsonReq["parent"] = {"id": folderId + '' };
  }
    else{
        jsonReq["parent"] = {"id": json2["parent"].id + '' };
        }


  if ( newFileName !== "" && newFileName !== null ){ 
    jsonReq["name"] = newFileName + '';
  }



  let url = "https://api.box.com/2.0/files/";
    url += fileId;
    url += "/copy";

  const response = httpClient.begin()
    .bearer(token)
    .body(JSON.stringify(jsonReq), "application/json; charset=UTF-8")
    .post(url);

  const status = response.getStatusCode();
  const responseTxt = response.getResponseAsString();
  let jsonRes = JSON.parse(responseTxt);
  try{
    if (status === 409){
      if(jsonRes["context_info"]["conflicts"][0]["type"] === "file"){
        jsonRes = jsonRes["context_info"]["conflicts"][0];
      }else{
        throw "";
      }
    }else if (status >= 300) {
      throw "";
    }
  }catch(e){
      const error = "Failed to copy \n status:" + status + "\n" + responseTxt;
      throw error;
    }

  engine.log("status:" + status);
  engine.log(responseTxt);

  return jsonRes;
}

}
