service-task-definition.script:
const SERVICE = 'bedrock';

function main() {
    ////// == 工程コンフィグ・ワークフローデータの参照 / Config & Data Retrieving ==
    const awsKey = configs.getObject('conf_AccessKey').getToken();
    const awsSecret = configs.getObject('conf_SecretKey').getToken();
    const region = retrieveRegion();
    const agent = configs.get('conf_Agent');
    const alias = configs.get('conf_Alias');
    const sessionDef = configs.getObject('conf_Session');
    let session = engine.findData(sessionDef);
    if (session === null) {
        session = `p${processInstance.getProcessInstanceId()}`;
        engine.setData(sessionDef, session);
    }
    const message = configs.get('conf_Message');

    ////// == 演算 / Calculating ==
    const answer = invokeAgent(awsKey, awsSecret, region, agent, alias, session, message);

    ////// == ワークフローデータへの代入 / Data Updating ==
    saveData('conf_Answer', answer);
}

/**
 * config からリージョンコードを読み出す
 * リージョンコードの形式として不正な場合はエラー
 * @return {String}
 */
function retrieveRegion() {
    const region = configs.get('conf_Region');
    // 今後リージョンが増えることも考えて、中央の最大文字数には余裕をみている
    const reg = new RegExp('^[a-z]{2}-[a-z]{4,16}-[1-9]$');
    if (!reg.test(region)) {
        throw new Error('Region Code is invalid.');
    }
    return region;
}

/**
 * モデルの実行
 * @param awsKey
 * @param awsSecret
 * @param region
 * @param agent
 * @param alias
 * @param session
 * @param message
 * @returns {String} answer
 */
const invokeAgent = (awsKey, awsSecret, region, agent, alias, session, message) => {
    const URL = `https://bedrock-agent-runtime.${region}.amazonaws.com/agents/${agent}/agentAliases/${alias}/sessions/${session}/text`;

    const payload = {
        enableTrace: false,
        endSession: false,
        inputText: message,
        sessionState: {
            promptSessionAttributes: {},
            sessionAttributes: {}
        }
    };

    const response = httpClient.begin()
        .awsSignV4(awsKey, awsSecret, region, SERVICE)
        .body(JSON.stringify(payload), 'application/json')
        .post(URL);

    const status = response.getStatusCode();
    const respTxt = response.getResponseAsString();
    if (status !== 200) {
        engine.log(respTxt);
        throw new Error(`Failed to invoke model. status: ${status}`);
    }

    const headerNames = response.getHeaderNames();
    for(let i = 0; i < headerNames.size(); i++) {
        const headerName = headerNames.get(i);
        engine.log(`${headerName}: ${response.getHeaderValues(headerName).get(0)}`);
    }
    const responseJson = JSON.parse(respTxt);
    engine.log(respTxt);

    return JSON.stringify(responseJson, undefined, 2);
};

/**
 * データ項目への保存
 * @param configName
 * @param data
 */
const saveData = (configName, data) => {
    const def = configs.getObject(configName);
    if (def === null) {
        return;
    }
    engine.setData(def, data);
};
