service-task-definition.script:
main();
function main(){
    const oauth2 = configs.get('conf_OAuth2');
    const fileId = decideFileId();
    applyWatermark(oauth2, fileId);
}

/**
  * ファイル ID を config から読み出して出力する
  * @return {String} fileId ファイル ID
  */
function decideFileId(){
    const fileId = engine.findData(configs.getObject('conf_FileId'));
    if (fileId === '' || fileId === null) {
        throw 'File ID is blank.';
    }
    return fileId;
}

/**
  * 電子すかしを適用
  * @param {String} oauth OAuth2 設定
  * @param {String} fileId ファイル ID
  */
function applyWatermark(oauth2, fileId) {
    const jsonBody = {};
    jsonBody['watermark'] = {'imprint': 'default'};
    const url = `https://api.box.com/2.0/files/${encodeURIComponent(fileId)}/watermark`;
    const response = httpClient.begin()
        .authSetting(oauth2)
        .body(JSON.stringify(jsonBody), 'application/json; charset=UTF-8')
        .put(url);
    const status = response.getStatusCode();
    if (status !== 200 && status !== 201) { // 200: 更新, 201: 新規適用
        engine.log(response.getResponseAsString());
        throw `Failed to apply watermark. status:${status}`;
    }
}

service-task-definition.test:
/**
 * 設定の準備
 * @param fileId
 */
const prepareConfigs = (fileId) => {
    configs.put('conf_OAuth2', 'Box');

    // ファイル ID を保存した文字型データ項目（単一行）を準備し、設定
    const fileIdDef = engine.createDataDefinition('ファイル ID', 1, 'q_FileId', 'STRING_TEXTFIELD');
    engine.setData(fileIdDef, fileId);
    configs.putObject('conf_FileId', fileIdDef);
}

/**
 * ファイル ID の値が空でエラー
 */
test('File ID is blank', () => {
    prepareConfigs(null);
    expect(execute).toThrow('File ID is blank.');
});

/**
 * API リクエストのテスト
 * @param {Object} request
 * @param request.url
 * @param request.method
 * @param request.contentType
 * @param request.body
 * @param fileId
 */
const assertRequest = ({url, method, contentType, body}, fileId) => {
    expect(url).toEqual(`https://api.box.com/2.0/files/${encodeURIComponent(fileId)}/watermark`);
    expect(method).toEqual('PUT');
    expect(contentType).toEqual('application/json; charset=UTF-8');
    const bodyObj = JSON.parse(body);
    expect(bodyObj.watermark.imprint).toEqual('default');
};

/**
 * API リクエストで失敗
 */
test('Fail in API Request', () => {
    prepareConfigs('fileId-1');

    httpClient.setRequestHandler((request) => {
        assertRequest(request, 'fileId-1');
        return httpClient.createHttpResponse(400, 'application/json', '{}');
    });

    expect(execute).toThrow('Failed to apply watermark. status:400');
});

/**
 * 電子すかし適用成功
 * 新規適用の場合
 */
test('Succeed to apply watermark', () => {
    prepareConfigs('fileId-1');

    httpClient.setRequestHandler((request) => {
        assertRequest(request, 'fileId-1');
        return httpClient.createHttpResponse(201, 'application/json', '{}');
    });

    execute();
});

/**
 * 電子すかし適用成功
 * 更新の場合
 */
test('Succeed to update watermark', () => {
    prepareConfigs('fileId-2');

    httpClient.setRequestHandler((request) => {
        assertRequest(request, 'fileId-2');
        return httpClient.createHttpResponse(200, 'application/json', '{}');
    });

    execute();
});
