/**
 * 既に共有リンクが存在する
 */
test("Shared link already exists", () => {
  configs.put("OAuth2", "Box");
  configs.put("FolderId", "12345");

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) {
      assertGetRequest(request, "12345");
      reqCount++;
      return httpClient.createHttpResponse(
        200,
        "application/json",
        JSON.stringify(HAS_SHARED_LINK)
      );
    }
    assertPutRequest(request, "12345");
    return httpClient.createHttpResponse(400, "application/json", "{}");
  });

  try {
    execute();
    fail("not come here");
  } catch (e) {
    expect(e.message).endsWith(
      "Shared link(https://www.box.com/s/vspke7y05sb214wjokpk) was already created."
    );
  }
});
