service-task-definition.script:
main();
function main() {
  //// == 自動工程コンフィグの参照 / Config Retrieving ==
  const myFilesDef = configs.getObject("File_DataId"); // (returns ProcessDataDefinitionView)
  const tableDataDef = configs.getObject("Table_DataId"); // (returns ProcessDataDefinitionView)
  const myFiles = engine.findData(myFilesDef);

  // ファイル型データに複数添付されている場合、エラー
  if (myFiles !== null && myFiles.size() > 1) {
    throw new Error("Attachment of multiple files can not be supported.");
  }
  const myTable = tableDataDef.createListArray();
  // ファイル型データにファイルが1つ添付されている場合
  if (myFiles.size() !== null) {
    // ファイル型を1行ずつ読み込んで、テーブル型にセット
    fileRepository.readFile(myFiles.get(0), "x-UTF-16LE-BOM", function (line) {
      const newRow = myTable.addRow();
      const cellsArray = line.split("\t");
      if (newRow.size() < cellsArray.length) {
        throw `The file has ${cellsArray.length} columns while the table has ${newRow.size()} columns.`;
      }
      for (let j = 0; j < newRow.size(); j++) {
        newRow.setCol(j, cellsArray[j]);
      }
    });
  }

  //// == ワークフローデータへの代入 / Data Updating ==
  // ファイル型データ項目にファイルが1つ添付されている場合はその値、1つも添付されていない場合は空をセット
  engine.setData(tableDataDef, myTable);
}

service-task-definition.test:
const prepareTableDef = () => {
  const tableDef = engine.createDataDefinition(
    "テーブル",
    10,
    "q_table",
    "LIST"
  );
  tableDef.addSubDataDefinition("文字", "string", "STRING");
  return tableDef;
};

const assertFile = (file, name, contentType) => {
  expect(file.getName()).toEqual(name);
  expect(file.getContentType()).toEqual(contentType);
};

test("success-1row-1column", () => {
  // ファイル型データ項目を準備して、config に指定
  const fileDef = engine.createDataDefinition("ファイル", 5, "q_file", "FILE");
  // ファイル型データ項目を挿入する
  configs.putObject("File_DataId", fileDef);

  // ArrayList を作成
  let files = new java.util.ArrayList();
  // ArrayList 型に CSV ファイルを追加
  files.add(
    // 文字コードUTF-16LE-BOM, タブ区切りで"hogehoge"という文字列の「テスト.csv」
    engine.createQfile("テスト.csv", "text/tab-separated-values; charset=x-UTF-16LE-BOM","hogehoge")
  );
  // ファイル型データ項目に値を指定
  engine.setData(fileDef, files);

  // テーブル型データ項目を準備して、config に指定
  const tableDef = prepareTableDef();
  // テーブル型データ項目を「Table_DataId」という名前で定義する
  configs.putObject("Table_DataId", tableDef);

  // テーブル型データ項目に ListArray を作成する
  const list = tableDef.createListArray();
  // 追加する行に加えるオブジェクトを定義する関数
  const addRow = (string) => {
    // リストに行を追加する
    const newRow = list.addRow();
    // 新しい行に文字列を追加する
    newRow.put("string", string);
  };
  // 文字列"hogehoge"を追加する
  addRow("hogehoge");
  // テーブル型データ項目に値を指定
  engine.setData(tableDef, list);
  // 実行
  execute();

  // ファイル型データ項目に値を指定
  const myTable = engine.findData(tableDef);

  // テーブル型データ項目の行数が1である
  const numOfRows = myTable.size(); // 行（Tableの高さ）
  expect(numOfRows).toEqual(1);
  // テーブル型データ項目の列数が1である
  const numOfCols = myTable.getRow(0).size(); // 列（Tableの幅）
  expect(numOfCols).toEqual(1);
  // テーブル型データ項目の0行０列目の値がhogehogeであることを確認
  expect(myTable.get(0,0)).toEqual("hogehoge");
});

test("success-2row-1column", () => {
  // ファイル型データ項目を準備して、config に指定
  const fileDef = engine.createDataDefinition("ファイル", 5, "q_file", "FILE");
  // ファイル型データ項目にオブジェクトを定義する
  configs.putObject("File_DataId", fileDef);

  // ArrayList 型に CSV ファイルを追加
  let files = new java.util.ArrayList();
  // 文字コードUTF-16LE-BOM, タブ区切りで"hogehoge"という文字列の「テスト.csv」
  files.add(
    engine.createQfile("テスト.csv", "text/tab-separated-values; charset=x-UTF-16LE-BOM","hogehoge\npiyopiyo")
  );
  // ファイル型データ項目に値を指定
  engine.setData(fileDef, files);

  // テーブル型データ項目を準備して、config に指定
  const tableDef = prepareTableDef();
  // テーブル型データ項目を「Table_DataId」という名前で定義する
  configs.putObject("Table_DataId", tableDef);

  // テーブル型データ項目に ListArray を作成する
  const list = tableDef.createListArray();
  // 追加する行に加えるオブジェクトを定義する関数
  const addRow = (string) => {
    // リストに行を追加する
    const newRow = list.addRow();
    // 新しい行に文字列を追加する
    newRow.put("string", string);
  };
  // 文字列"hogehoge"と"piyopiyo"を追加する
  addRow("hogehoge");
  addRow("piyopiyo");
  // テーブル型データ項目に値を指定
  engine.setData(tableDef, list);
  // 実行
  execute();

  // テーブル型データ項目の値を取り出す
  const myTable = engine.findData(tableDef);

  // テーブル型データ項目の行数が1である
  const numOfRows = myTable.size(); // 行（Tableの高さ）
  expect(numOfRows).toEqual(2);
  // テーブル型データ項目の列数が1である
  const numOfCols = myTable.getRow(0).size(); // 列（Tableの幅）
  expect(numOfCols).toEqual(1);
  // テーブル型データ項目の1行1列目と2行1列目の値がhogehogeであることを確認
  expect(myTable.get(0, 0)).toEqual("hogehoge");
  expect(myTable.get(1, 0)).toEqual("piyopiyo");
});

test("overwrite-2row-1column", () => {
  // ファイル型データ項目を作成
  const fileDef = engine.createDataDefinition("ファイル", 5, "q_file", "FILE");
  // ファイル型データ項目にオブジェクトを定義する
  configs.putObject("File_DataId", fileDef);

  // ArrayList を作成
  let files = new java.util.ArrayList();
  // ArrayList 型に CSV ファイルを追加
  files.add(
    // 文字コードUTF-16LE-BOM, タブ区切りで"hogehoge"という文字列の「テスト.csv」
    engine.createQfile("テスト.csv", "text/tab-separated-values; charset=x-UTF-16LE-BOM","hogehoge\npiyopiyo")
  );
  // ファイル型データ項目に値を指定する
  engine.setData(fileDef, files);

  // テーブル型データ項目を準備して、config に指定
  const tableDef = prepareTableDef();
  // テーブル型データ項目を「Table_DataId」という名前で定義する
  configs.putObject("Table_DataId", tableDef);

  // テーブル型データ項目に ListArray を作成する
  const list = tableDef.createListArray();
  // 追加する行に加えるオブジェクトを定義する関数
  const addRow = (string) => {
    // 行を追加する
    const newRow = list.addRow();
    // 新しい行に文字列を追加する
    newRow.put("string", string);
  };
  // 空白文字列を追加する
  addRow("");
  addRow("");
  // テーブル型データ項目にセットする
  engine.setData(tableDef, list);
  // 実行
  execute();

  // テーブル型データ項目の値を取る
  const myTable = engine.findData(tableDef);

  // テーブル型データ項目の行数が1である
  const numOfRows = myTable.size(); // 行（Tableの高さ）
  expect(numOfRows).toEqual(2);
  // テーブル型データ項目の列数が1である
  const numOfCols = myTable.getRow(0).size(); // 列（Tableの幅）
  expect(numOfCols).toEqual(1);
  // テーブル型データ項目の1行1列目と2行1列目の値が空白であることを確認
  expect(myTable.get(0, 0)).toEqual("hogehoge");
  expect(myTable.get(1, 0)).toEqual("piyopiyo");
});

test("success-1row-2column", () => {
  // ファイル型データ項目を作成
  const fileDef = engine.createDataDefinition("ファイル", 5, "q_file", "FILE");
  // ファイル型データ項目にオブジェクトを定義する
  configs.putObject("File_DataId", fileDef);

  // ArrayList を作成
  let files = new java.util.ArrayList();
  // ArrayList 型に文字コードUTF-16LE-BOM, タブ区切りで"hogehoge\npiyopiyo"という文字列の「テスト.csv」ファイルを作成。
  files.add(
    engine.createQfile("テスト.csv", "text/tab-separated-values; charset=x-UTF-16LE-BOM","hogehoge\t123")
  );
  // ファイル型データ項目をセットする
  engine.setData(fileDef, files);

  // テーブル型データ項目を定義する
  const tableDef = prepareTableDef();
  // テーブル型データ項目を「Table_DataId」という名前で定義する
  configs.putObject("Table_DataId", tableDef);

  // テーブル型データ項目に ListArray を作成する
  const list = tableDef.createListArray();
  // 追加する行に加えるオブジェクトを定義する関数
  const addRow = (string, decimal) => {
    // 行を追加する
    const newRow = list.addRow();
    // 新しい行に文字列を追加する
    newRow.put("string", string);
  };
  addRow("","");
  // テーブル型データ項目にセットする
  engine.setData(tableDef, list);

  execute();

  // テーブル型データ項目を
  const myTable = engine.findData(tableDef);

  // テーブル型データ項目の行数が1である
  const numOfRows = myTable.size(); // 行（Tableの高さ）
  expect(numOfRows).toEqual(1);
  // テーブル型データ項目の列数が1である
  const numOfCols = myTable.getRow(0).size(); // 列（Tableの幅）
  expect(numOfCols).toEqual(1);
  // テーブル型データ項目の1行1列目と1行2列目の値がhogehogeであることを確認
  expect(myTable.get(0, 0)).toEqual("hogehoge");
  expect(myTable.get(0, 1)).toEqual("123");
});

test("multiple files", () => {
  const fileDef = engine.createDataDefinition("ファイル", 5, "q_file", "FILE");
  configs.putObject("File_DataId", fileDef);

  const files = new java.util.ArrayList();
  files.add(
    engine.createQfile("1.csv", "text/tab-separated-values", "テスト1")
  );
  files.add(
    engine.createQfile("2.csv", "text/tab-separated-values", "テスト2")
  );
  engine.setData(fileDef, files);

  try {
    execute();
    fail("not come here");
  } catch (e) {
    expect(e.message).endsWith(
      "Attachment of multiple files can not be supported."
    );
  }
});
