//////// START "main()" ////////
main();
function main(){ 

//// == Config Retrieving / 工程コンフィグの参照 ==
var textdataId = configs.get( "Text_DataId" ) + "";
var filename = configs.get( "File_Name" ) + "";
var filedataId = configs.get( "File_DataId" ) + "";
// 'java.lang.String' (String Obj) to javascript primitive 'string'


if(filename === "" ||filename === null) {
    throw new Error( "File Name is blank" );
  }



//// == Data Retrieving / ワークフローデータの参照 ==
var myText = engine.findDataByNumber( textdataId ); 
var myFiles  = engine.findDataByNumber( filedataId );// java.util.ArrayList
if( myFiles === null ){ myFiles = new java.util.ArrayList(); }


var myString = "";

if( myText !== null){
myString += myText
}


myFiles.add(
  new com.questetra.bpms.core.event.scripttask.NewQfile(
    filename,
    "text/tab-separated-values; charset=x-UTF-16LE-BOM",
    myString
  )
);




//// == Data Updating / ワークフローデータへの代入 ==
engine.setDataByNumber( filedataId, myFiles );


} //////// END "main()" ////////
