service-task-definition.script:
// OAuth2 config sample at [OAuth 2.0 Setting]
// - Authorization Endpoint URL: https://login.microsoftonline.com/common/oauth2/v2.0/authorize
// - Token Endpoint URL: https://login.microsoftonline.com/common/oauth2/v2.0/token
// - Scope: https://graph.microsoft.com/Files.ReadWrite offline_access
// - Consumer Key: (Get by Microsoft Azure Active Directory)
// - Consumer Secret: (Get by Microsoft Azure Active Directory)


//// == Config Retrieving / 工程コンフィグの参照 ==
main();
function main(){
  const oauth2 = configs.get( "conf_OAuth2" ) + "";
  const bookPath = configs.get( "conf_Path" ) + "";
  const sheetName = configs.get( "conf_Title" ) + "";
  let values = [];
  values[0] = configs.get( "conf_ValueA" ) + "";
  values[1] = configs.get( "conf_ValueB" ) + "";
  values[2] = configs.get( "conf_ValueC" ) + "";
  values[3] = configs.get( "conf_ValueD" ) + "";
  values[4] = configs.get( "conf_ValueE" ) + "";
  values[5] = configs.get( "conf_ValueF" ) + "";
  values[6] = configs.get( "conf_ValueG" ) + "";
  values[7] = configs.get( "conf_ValueH" ) + "";
  values[8] = configs.get( "conf_ValueI" ) + "";
  values[9] = configs.get( "conf_ValueJ" ) + "";
  // convert 'java.lang.String' to 'javascript string'

  //// == Calculating / 演算 ==
  // Get OAuth2 Token
  const token  = httpClient.getOAuth2Token( oauth2 );
  if(bookPath === ""){
    throw "Book Path is empty."
  }
  if(bookPath[0] !== "/"){
    throw "Book Path is invalid."
  }
  // Access to the API 1st(Get Book ID)
  const getIdUrl = `https://graph.microsoft.com/v1.0/me/drive/root:${bookPath}`;
  //Path must start with "/".
  let getBookId = httpClient.begin()
    .bearer(token)
    .get(getIdUrl); 

  const responseA = getBookId.getResponseAsString();
  const statusA = getBookId.getStatusCode();
  let accessLog = `---GET request--- 
  ${statusA}
  ${responseA}
  `;
  if(statusA >= 300){
    throw accessLog;
  }
  const responseAjson = JSON.parse(responseA);
  const bookId = responseAjson.id;

  // Access to the API 2nd(Get UsedRange)
  const getRangeUrl = `https://graph.microsoft.com/v1.0/me/drive/items/${bookId}/workbook/worksheets/${sheetName}/range(address='${sheetName}!1:1048576')/usedRange`;
  let getUsedRange = httpClient.begin()
    .bearer(token)
    .get (getRangeUrl);

  const responseB = getUsedRange.getResponseAsString();
  const statusB = getUsedRange.getStatusCode();
  accessLog += `---GET request--- 
  ${statusB}
  ${responseB}
  `;
  if(statusB >= 300){
    throw accessLog;
  }
  const responseBjson = JSON.parse(responseB);
  const usedRange = responseBjson.address;
  const usedRangeLastCellArray = usedRange.match(/:\D+\d+/);
  if(usedRangeLastCellArray === null){
    throw "All rows are already used.";
  }
  const usedRangeLastCell = usedRangeLastCellArray[0];
  const addedRow = parseInt(usedRangeLastCell.match(/\d+/)[0], 10) + 1;

  let requestObj = {
    values : [[]]
  };
  let count = 0;
  engine.log("148");
  for(let i = 0; i < 10; i++){
    if(values[i] === "" || values[i] === null){
      requestObj.values[0].push(null);
      count++;
    }else{
      if(values[i].length > 32767){
        throw "Can't set text over 32,767 character.";
      }
      requestObj.values[0].push(values[i]);
    }
  }
  if(count === 10){
    throw "No Data to add is set.";
  }
  engine.log("163");
  // Access to the API 3rd(PATCH)
  const patchUri = `https://graph.microsoft.com/v1.0/me/drive/items/${bookId}/workbook/worksheets/${sheetName}/range(address='${sheetName}!A${addedRow}:J${addedRow}')/`;
  let patchToSheet = httpClient.begin()
    .bearer(token)
    .body(JSON.stringify( requestObj ), "application/json")
    .patch(patchUri); 

  const responseC = patchToSheet.getResponseAsString();
  const statusC = patchToSheet.getStatusCode();
  accessLog += `---PATCH request--- 
  ${statusC}
  ${responseC}`;
  if( statusC != 200 ){
      throw accessLog;
    }

  //// == Output Log / ログ出力 ==
  engine.log(accessLog);
}
