/**
 * メール取得成功
 * 添付ファイル 2 つのマルチパートメール
 * 本文パートは text/html
 * To, Cc, Bcc はそれぞれ複数件
 */
test('200 Success - Multipart message with two attachments', () => {
  const messageId = 'messageId-2';
  const dataDefs = prepareConfigs(messageId);

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) { // メール取得の GET リクエスト
      assertGetMessageRequest(request, messageId);
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_MESSAGE_2));
    }
    if (reqCount == 1) { // 添付ファイル取得の GET リクエスト 1 回目
      assertGetAttachmentRequest(request, messageId, 'attachmentId-1');
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_1));
    }
    // 添付ファイル取得の GET リクエスト 2 回目
    assertGetAttachmentRequest(request, messageId, 'attachmentId-2');
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_2));
  });

  // <script> のスクリプトを実行
  execute();

  // 保存先データ項目の値をチェック
  expect(engine.findData(dataDefs.fromAddress)).toEqual('From2@example.com');
  expect(engine.findData(dataDefs.fromName)).toEqual('From 表示名2');

  const recipientAddresses = 'To@example.com\nTo2@example.com\n'
    + 'Cc@example.com\nCc2@example.com\n'
    + 'Bcc@example.com\nBcc2@example.com';
  const recipientNames = 'To 表示名\nTo 表示名2\n'
    + 'Cc 表示名\nCc 表示名2\n'
    + 'Bcc 表示名\nBcc 表示名2';
  expect(engine.findData(dataDefs.recipientAddresses)).toEqual(recipientAddresses);
  expect(engine.findData(dataDefs.recipientNames)).toEqual(recipientNames);

  const datetimeString = dateFormatter.format('GMT+0900', 'yyyy年MM月dd日 HH時mm分', engine.findData(dataDefs.sentDatetime));
  expect(datetimeString).toEqual('2022年03月26日 20時25分');

  expect(engine.findData(dataDefs.subject)).toEqual('件名2');
  expect(engine.findData(dataDefs.body)).toEqual('<h1>テストメール2です。</h1>\n<p>これはテストメール2です。</p>');
  const files = engine.findData(dataDefs.attachments);
  expect(files.length).toEqual(2); // 事前ファイルは削除される
  expect(files[0].getName()).toEqual('添付ファイル1.png');
  expect(files[0].getLength()).toEqual(SAMPLE_ATTACHMENT_1.size);
  expect(files[0].getContentType()).toEqual('image/png');
  expect(files[1].getName()).toEqual('添付ファイル2.txt');
  expect(files[1].getLength()).toEqual(SAMPLE_ATTACHMENT_2.size);
  expect(files[1].getContentType()).toEqual('text/plain');
});

/**
 * シングルパートメール
 * 本文は text/plain で、元のエンコードは Shift_JIS
 * From ヘッダに表示名なし
 * To, Cc, Bcc ヘッダに表示名がない宛先を含む
 */
const SAMPLE_MESSAGE_3 = {
  "id": "messageId-3",
  "threadId": "threadId",
  "labelIds": [
    "UNREAD",
    "INBOX"
  ],
  "snippet": "テストメール3です。",
  "payload": {
    "partId": "",
    "mimeType": "text/plain",
    "filename": "",
    "headers": [
      {
        "name": "Date",
        "value": "Mon, 7 Mar 2022 10:00:00 +0900 (GMT+09:00)"
      },
      {
        "name": "From",
        "value": "\u003cFrom@example.com\u003e"
      },
      {
        "name": "To",
        "value": "\"To 表示名\" \u003cTo@example.com\u003e, \u003cTo2@example.com\u003e"
      },
      {
        "name": "Cc",
        "value": "\u003cCc@example.com\u003e, \"Cc 表示名2\" \u003cCc2@example.com\u003e"
      },
      {
        "name": "Bcc",
        "value": "\u003cBcc@example.com\u003e, \"Bcc 表示名2\" \u003cBcc2@example.com\u003e"
      },
      {
        "name": "Subject",
        "value": "件名3"
      },
      {
        "name": "Content-Type",
        "value": "text/plain; charset=\"Shift_JIS\""
      }
    ],
    "body": {
      // Shift_JIS の本文も、 Gmail API のレスポンスでは UTF-8 を base64 エンコードしたものになる
      "data": base64.encodeToUrlSafeString('テストメール3です。\nこれはテストメール3です。')
    }
  }
};

/**
 * メール取得成功
 * シングルパートメール
 * 本文は text/plain で、元のエンコードは Shift_JIS
 * From ヘッダに表示名なし
 * To, Cc, Bcc ヘッダに表示名がない宛先を含む
 */
test('200 Success - Single-part message with text/plain body encoded in Shift_JIS', () => {
  const messageId = 'messageId-3';
  const dataDefs = prepareConfigs(messageId);

  httpClient.setRequestHandler((request) => {
    assertGetMessageRequest(request, messageId);
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_MESSAGE_3));
  });

  // <script> のスクリプトを実行
  execute();

  // 保存先データ項目の値をチェック
  expect(engine.findData(dataDefs.fromAddress)).toEqual('From@example.com');
  expect(engine.findData(dataDefs.fromName)).toEqual(null); // From に表示名なし

  const recipientAddresses = 'To@example.com\nTo2@example.com\n'
    + 'Cc@example.com\nCc2@example.com\n'
    + 'Bcc@example.com\nBcc2@example.com';
  const recipientNames = 'To 表示名\n\n' // To 2 つめの表示名はなし
    + '\nCc 表示名2\n' // Cc 1 つめの表示名はなし
    + '\nBcc 表示名2'; // Bcc 1 つめの表示名はなし
  expect(engine.findData(dataDefs.recipientAddresses)).toEqual(recipientAddresses);
  expect(engine.findData(dataDefs.recipientNames)).toEqual(recipientNames);

  const datetimeString = dateFormatter.format('GMT+0900', 'yyyy年MM月dd日 HH時mm分', engine.findData(dataDefs.sentDatetime));
  expect(datetimeString).toEqual('2022年03月07日 10時00分');

  expect(engine.findData(dataDefs.subject)).toEqual('件名3');
  expect(engine.findData(dataDefs.body)).toEqual('テストメール3です。\nこれはテストメール3です。');
  const files = engine.findData(dataDefs.attachments);
  expect(files.length).toEqual(0); // 事前ファイルは削除される
});

