/**
 * 添付ファイル数が HTTP リクエストの制限数を超える
 */
test('Number of HTTP requests is over the limit', () => {
  const messageId = 'messageId-1';
  prepareConfigs(messageId);

  const message = {...SAMPLE_MESSAGE_1};
  const httpRequestLimit = httpClient.getRequestingLimit();

  // もともと SAMPLE_MESSAGE_1 には添付ファイルが 1 つある
  // 添付ファイルが合計 httpRequestLimit 個になるように追加する
  for (let i = 0; i < httpRequestLimit - 1; i++) {
    message.payload.parts[0].parts.push(ATTACHMENT_PART);
  }

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) { // メール取得の GET リクエスト
      assertGetMessageRequest(request, messageId);
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(message));
    }
    // 添付ファイル取得の GET リクエスト
    assertGetAttachmentRequest(request, messageId, 'attachmentId-1');
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_1));
  });

  expect(execute).toThrow('Number of HTTP requests is over the limit.');
});

/**
 * 添付ファイル数が HTTP リクエストの制限数におさまる
 */
test('200 Success - Number of HTTP requests is within the limit', () => {
  const messageId = 'messageId-1';
  const dataDefs = prepareConfigs(messageId);

  const message = {...SAMPLE_MESSAGE_1};
  const httpRequestLimit = httpClient.getRequestingLimit();

  // もともと SAMPLE_MESSAGE_1 には添付ファイルが 1 つある
  // 添付ファイルが合計 httpRequestLimit - 1 個になるように追加する
  for (let i = 0; i < httpRequestLimit - 2; i++) {
    message.payload.parts[0].parts.push(ATTACHMENT_PART);
  }

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) { // メール取得の GET リクエスト
      assertGetMessageRequest(request, messageId);
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(message));
    }
    // 添付ファイル取得の GET リクエスト
    assertGetAttachmentRequest(request, messageId, 'attachmentId-1');
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_1));
  });

  // <script> のスクリプトを実行
  execute();

  // 保存されているファイルの個数をチェック。事前ファイルは削除されるので、合計 httpRequestLimit - 1 個のはず
  const files = engine.findData(dataDefs.attachments);
  expect(files.length).toEqual(httpRequestLimit - 1);
});

/**
 * メール取得成功
 * ファイル名のない添付ファイルは、デフォルトの名前で保存される
 */
test('200 Success - Attachment has no name', () => {
  const messageId = 'messageId-1';
  const dataDefs = prepareConfigs(messageId);

  const attachmentPart = {...ATTACHMENT_PART};
  delete attachmentPart.filename; // ファイル名を削除
  const message = {...SAMPLE_MESSAGE_1};
  message.payload.parts[0].parts[1] = attachmentPart; // 添付ファイルを置き換え

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) { // メール取得の GET リクエスト
      assertGetMessageRequest(request, messageId);
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(message));
    }
    // 添付ファイル取得の GET リクエスト
    assertGetAttachmentRequest(request, messageId, 'attachmentId-1');
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_1));
  });

  // <script> のスクリプトを実行
  execute();

  // 保存先データ項目の値をチェック
  const files = engine.findData(dataDefs.attachments);
  expect(files.length).toEqual(1); // 事前ファイルは削除される
  expect(files[0].getName()).toEqual('noname'); // ファイル名はデフォルトのものが使われる
  expect(files[0].getLength()).toEqual(SAMPLE_ATTACHMENT_1.size);
  expect(files[0].getContentType()).toEqual('image/png');
});

/**
 * メール取得成功
 * Content-Type のない添付ファイルは、デフォルトの Content-Type で保存される
 */
test('200 Success - Attachment has no Content-Type', () => {
  const messageId = 'messageId-1';
  const dataDefs = prepareConfigs(messageId);

  const attachmentPart = {...ATTACHMENT_PART};
  attachmentPart.headers = attachmentPart.headers.filter(header => header.name !== 'Content-Type');
  const message = {...SAMPLE_MESSAGE_1};
  message.payload.parts[0].parts[1] = attachmentPart; // 添付ファイルを置き換え

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) { // メール取得の GET リクエスト
      assertGetMessageRequest(request, messageId);
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(message));
    }
    // 添付ファイル取得の GET リクエスト
    assertGetAttachmentRequest(request, messageId, 'attachmentId-1');
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_1));
  });

  // <script> のスクリプトを実行
  execute();

  // 保存先データ項目の値をチェック
  const files = engine.findData(dataDefs.attachments);
  expect(files.length).toEqual(1); // 事前ファイルは削除される
  expect(files[0].getName()).toEqual('添付ファイル.png');
  expect(files[0].getLength()).toEqual(SAMPLE_ATTACHMENT_1.size);
  expect(files[0].getContentType()).toEqual('image/png'); // ファイル名から推定された content-type
});

