  expect(files.length).toEqual(1); // 事前ファイルは削除される
  expect(files[0].getName()).toEqual('添付ファイル.png');
  expect(files[0].getLength()).toEqual(SAMPLE_ATTACHMENT_1.size);
  expect(files[0].getContentType()).toEqual('image/png');
});

/**
 * メール取得成功
 * 添付ファイルを保存するデータ項目が設定されていない場合、添付ファイルを取得するリクエストは発行されない
 */
test('200 Success - Attachments not saved', () => {
  const messageId = 'messageId-1';
  const dataDefs = prepareConfigs(messageId);
  configs.put('conf_attachments', ''); // データ項目の設定を解除

  httpClient.setRequestHandler((request) => { // メール取得の GET リクエストのみ発行される
    assertGetMessageRequest(request, messageId);
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_MESSAGE_1));
  });

  // <script> のスクリプトを実行
  execute();

  // データ項目の値をチェック。事前ファイルは残ったまま
  const files = engine.findData(dataDefs.attachments);
  expect(files.length).toEqual(1);
  expect(files[0].getName()).toEqual('事前ファイル.csv');
});

/**
 * メール取得成功
 * 添付ファイルを保存するデータ項目に、もともとファイルが添付されていなかった場合
 */
test('200 Success - No files were pre-set', () => {
  const messageId = 'messageId-1';
  prepareConfigs(messageId);

  // 新たな dataDef を作って、configs.putObject()
  const fileDataDef = engine.createDataDefinition('新しいファイル型データ項目', 10, 'q_newFile', 'FILE');
  configs.putObject('conf_attachments', fileDataDef);

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) { // メール取得の GET リクエスト
      assertGetMessageRequest(request, messageId);
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_MESSAGE_1));
    }
    // 添付ファイル取得の GET リクエスト
    assertGetAttachmentRequest(request, messageId, 'attachmentId-1');
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_1));
  });

  // <script> のスクリプトを実行
  execute();

  // 保存先データ項目の値をチェック。ファイルが追加されている
  const files = engine.findData(fileDataDef);
  expect(files.length).toEqual(1);
  expect(files[0].getName()).toEqual('添付ファイル1.png');
  expect(files[0].getLength()).toEqual(SAMPLE_ATTACHMENT_1.size);
  expect(files[0].getContentType()).toEqual('image/png');
});

/**
 * メール取得成功
 * 設定されていないデータ項目がある
 */
test('200 Success - Some data items are not set', () => {
  const messageId = 'messageId-1';
  const dataDefs = prepareConfigs(messageId);

  // C3, C6, C9 を空にする
  configs.put('conf_fromAddress', '');
  configs.put('conf_recipientNames', '');
  configs.put('conf_body', '');

  let reqCount = 0;
  httpClient.setRequestHandler((request) => {
    if (reqCount === 0) { // メール取得の GET リクエスト
      assertGetMessageRequest(request, messageId);
      reqCount++;
      return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_MESSAGE_1));
    }
    // 添付ファイル取得の GET リクエスト
    assertGetAttachmentRequest(request, messageId, 'attachmentId-1');
    return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_ATTACHMENT_1));
  });

  // <script> のスクリプトを実行
  execute();

  // 保存先データ項目の値をチェック。設定されたデータ項目にのみ値が保存される
  expect(engine.findData(dataDefs.fromAddress)).toEqual('事前文字列');
  expect(engine.findData(dataDefs.fromName)).toEqual('From 表示名');

  const recipientAddresses = 'To@example.com\n'
    + 'Cc@example.com\n'
    + 'Bcc@example.com';
  expect(engine.findData(dataDefs.recipientAddresses)).toEqual(recipientAddresses);
  expect(engine.findData(dataDefs.recipientNames)).toEqual('事前\n文字列');

  const datetimeString = dateFormatter.format('GMT+0900', 'yyyy年MM月dd日 HH時mm分', engine.findData(dataDefs.sentDatetime));
  expect(datetimeString).toEqual('2022年03月07日 10時00分');

  expect(engine.findData(dataDefs.subject)).toEqual('件名');
  expect(engine.findData(dataDefs.body)).toEqual('事前\n文字列');
  const files = engine.findData(dataDefs.attachments);
  expect(files.length).toEqual(1); // 事前ファイルは削除される
  expect(files[0].getName()).toEqual('添付ファイル1.png');
  expect(files[0].getLength()).toEqual(SAMPLE_ATTACHMENT_1.size);
  expect(files[0].getContentType()).toEqual('image/png');
});
