service-task-definition.script:
// OAuth2 config
// Authorization Endpoint URL: https://accounts.google.com/o/oauth2/auth?access_type=offline&approval_prompt=force
// Token Endpoint URL: https://accounts.google.com/o/oauth2/token
// Scope: https://www.googleapis.com/auth/spreadsheets
// Consumer Key: (Get by Google Developers Console)
// Consumer Secret: (Get by Google Developers Console)
main();
function main(){
  //// == 工程コンフィグの参照 / Config Retrieving ==
  const oauth2 = configs.get("conf_OAuth2");

  const sheetId = configs.get("conf_sheetId");
  if (sheetId === "" || sheetId === null) {
    throw "Souce Sheet ID isn't set";
  }

  const sheetTitle = configs.get("conf_sheetTitle");
  addTab(oauth2, sheetId, sheetTitle);
}


/**
 * Google スプレッドシートの選択肢データを取得
 * @param {String} oauth2 OAuth2 認証設定
 * @param {String} sheetId スプレッドシートの ID
 * @param {String} sheetTitle シートのタイトル
 */
function addTab(oauth2, sheetId, sheetTitle) {
  // Method: spreadsheets.values.batchGet
  const uri = `https://sheets.googleapis.com/v4/spreadsheets/${sheetId}/:batchUpdate`;

  let requestObj = {
    requests: []
  };

  requestObj.requests[0] = {
    "addSheet": {
      "properties": {
        "title": sheetTitle
      }
    }
  };

  const response = httpClient.begin()
    .authSetting(oauth2)
    .body(JSON.stringify(requestObj), "application/json")
    .post(uri);

  const status = response.getStatusCode();
  const responseStr = response.getResponseAsString();
  if (status >= 300) {
        engine.log(`Status: ${status}`);
        engine.log(responseStr);
        throw `Failed to create tabs.`;
  }
  engine.log("Succeeded to create Sheet");
}
