service-task-definition.script:
// Append Table Records to Google Sheets (ver. 20160915)
// (c) 2016, Questetra, Inc. (the MIT License)

// OAuth2 config
// Authorization Endpoint URL: https://accounts.google.com/o/oauth2/auth?access_type=offline&approval_prompt=force
// Token Endpoint URL: https://accounts.google.com/o/oauth2/token
// Scope: https://www.googleapis.com/auth/spreadsheets
// Consumer Key: (Get by Google Developers Console)
// Consumer Secret: (Get by Google Developers Console)
main();
function main(){
  //// == 自動工程コンフィグの参照 / Config Retrieving ==
  let dataidArray = [];
  const dataIdT = configs.get( "conf_DataIdT" ); // (returns Number)
  const oauth2 = configs.get( "conf_OAuth2" );
  const spreadsheetId = configs.get( "conf_DataIdW" ) + "";
  const sheetName = configs.get( "conf_DataIdX" ) + "";
  dataidArray[0] = configs.get( "conf_DataIdA" );
  dataidArray[1] = configs.get( "conf_DataIdB" );
  dataidArray[2] = configs.get( "conf_DataIdC" );
  dataidArray[3] = configs.get( "conf_DataIdD" );
  dataidArray[4] = configs.get( "conf_DataIdE" );
  dataidArray[5] = configs.get( "conf_DataIdF" );
  dataidArray[6] = configs.get( "conf_DataIdG" );
  dataidArray[7] = configs.get( "conf_DataIdH" );
  dataidArray[8] = configs.get( "conf_DataIdI" );
  dataidArray[9] = configs.get( "conf_DataIdJ" );
  let dataidCheck = 0;
  for(let i = 0; i < 10; i++){
    if(dataidArray[i] === null || dataidArray[i] === ""){
      dataidCheck++;
    }
  }

  if(dataidCheck === 10){
    throw "No Data to add is selected.";
  }
  //// == ワークフローデータの参照 / Data Retrieving ==
  const tableData = engine.findDataByNumber( dataIdT );
  // tableData: com.questetra.bpms.core.model.formdata.ListArray
  if(tableData === null){
    throw "the table data is empty.";
  }
  tableData = engine.findDataDefinitionByNumber( dataIdT );
  tableDataList = tableData.getSubDataDefinitions();
  //// == 演算 / Calculating ==
  let accessLog = "";
  let sheetId = -1;
  const token = httpClient.getOAuth2Token( oauth2 );

  try{
    // check spreadsheet
    checkSheet(token,spreadsheetId,accessLog,sheetName,sheetId);
    // add sheet (not exsist)
    if( sheetId == -1 ){  
      addSheet(token,spreadsheetId,accessLog,sheetName,sheetId);
    }

    // append records
    if( sheetId >= 0 ){
      if( tableData !== null){
        let Obj = {};
        makeAppendData(tableData,sheetId,tableDataList,Obj);
        sendAppendData(token,spreadsheetId,accessLog,Obj);
      }
    }
  }catch(e){
    accessLog = e + accessLog;
    throw accessLog;
 }
  //// == ワークフローデータへの代入 / Data Updating ==
  engine.log(accessLog );
}

function checkSheet(token,spreadsheetId,accessLog,sheetName,sheetId){
  let uri = "https://sheets.googleapis.com/v4/spreadsheets/";
    uri += spreadsheetId;
    uri += "?includeGridData=false";
  const response = httpClient.begin()
    .bearer(token)
    .get( uri ); 
  accessLog += "---GET request--- " + response.getStatusCode() + "\n";
  let responseJson = response.getResponseAsString();
  if( response.getStatusCode() == 200 ){
    let jsonObj = JSON.parse( responseJson );
    accessLog += " SPREADSHEET TITLE: \"" + jsonObj.properties.title + "\" has";
    accessLog += jsonObj.sheets.length + " sheets\n";
    for( var i = 0; i < jsonObj.sheets.length; i++ ){
      if( jsonObj.sheets[i].properties.title == sheetName ){
        sheetId = parseInt( jsonObj.sheets[i].properties.sheetId, 10 );
        accessLog += " SHEET TITLE: \"" + sheetName + "\" exists (id: " + sheetId + ")\n";
      }else{
        accessLog += " SHEET TITLE: \"" + sheetName + "\" does not exist\n";
      }
    }
  }else{
    accessLog += responseJson;
    throw "Can't get sheet information";
  }
}
function addSheet(token,spreadsheetId,sheetId,sheetName,accessLog){
  let uri = "https://sheets.googleapis.com/v4/spreadsheets/";
    uri += spreadsheetId;
    uri += ":batchUpdate";

  sheetId = processInstance.getProcessInstanceId() - 0;
  let Obj = {};
  Obj.requests = []; // Array
  Obj.requests[0] = {};
  Obj.requests[0].addSheet = {};
  Obj.requests[0].addSheet.properties = {};
  Obj.requests[0].addSheet.properties.sheetId = sheetId;
  Obj.requests[0].addSheet.properties.title = sheetName;

  const response = httpClient.begin()
    .bearer(token)
    .body( JSON.stringify( Obj ), "application/json" )
    .post( uri ); 
  accessLog += "---POST request--- " + response.getStatusCode() + "\n";
  if( response.getStatusCode() == 200 ){
     accessLog += " SHEET TITLE: \"" + sheetName + "\" created (id: " + sheetId + ")\n";
  }
}

function makeAppendData(table,sheetId,tableDataList,Obj){
  const numOfRows = table.size() - 0; // 行（Tableの高さ）
  Obj.requests = []; // Array
  Obj.requests[0] = {};
  Obj.requests[0].appendCells = {};
  Obj.requests[0].appendCells.rows = []; // Array
  Obj.requests[0].appendCells.sheetId = sheetId;
  Obj.requests[0].appendCells.fields = "*";

  let dataNumberList = [];
  for(let i = 0;i < tableDataList.length; i++){
    let temp = tableDataList[i].getNumber() + "";
    dataNumberList.push(temp);
  }
  let pointedCol = 0;
  for( let i = 0; i < numOfRows; i++ ){
    Obj.requests[0].appendCells.rows[i] = {};
    Obj.requests[0].appendCells.rows[i].values = []; // Array
    for( let j = 0; j < 10; j++ ){
      if(dataidArray[j] !== "" && dataidArray[j] !== null ){
        pointedCol = dataNumberList.indexOf(dataidArray[j]);
        let cellValue = table.get(i, pointedCol );
        if(cellValue.length > 50000){
          throw "Can't set text over 50,000 character.";
        }
        if(tableDataList[pointedCol].matchDataType("DECIMAL")){
          Obj.requests[0].appendCells.rows[i].values[j] = {"userEnteredValue": {"numberValue": cellValue }}; 
        }else{
          Obj.requests[0].appendCells.rows[i].values[j] = {"userEnteredValue": {"stringValue": cellValue }};
        }
      }else{
        Obj.requests[0].appendCells.rows[i].values[j] = {"userEnteredValue": {"stringValue": "" }};
      }
    }
    let count = 0;
    let flag = 1;
    for(let k = 9; k > -1; k--){
      if((Obj.requests[0].appendCells.rows[i].values[k].userEnteredValue.stringValue == null || 
          Obj.requests[0].appendCells.rows[i].values[k].userEnteredValue.stringValue == "") && 
          (Obj.requests[0].appendCells.rows[i].values[k].userEnteredValue.numberValue == null ||
          Obj.requests[0].appendCells.rows[i].values[k].userEnteredValue.numberValue == "")){
        count++;
        if(flag){
          Obj.requests[0].appendCells.rows[i].values[k] = null;
        }
      }else{
        flag = 0;
      }
    }
    if(count == 10){
      throw "All selected data are empty.";
    }
  }
}

function sendAppendData(token,spreadsheetId,accessLog,Obj){
  let uri = "https://sheets.googleapis.com/v4/spreadsheets/";
      uri += spreadsheetId;
      uri += ":batchUpdate";

  let response = httpClient.begin()
    .bearer(token)
    .body( JSON.stringify( Obj ), "application/json" )
    .post( uri ); 
  accessLog += "---POST request--- " + response.getStatusCode() + "\n";
  if( response.getStatusCode() == 200 ){
    accessLog += "ADDED DATA";
  }else if( response.getStatusCode() >= 300 ){
    throw response.getResponseAsString();
  }
}
