service-task-definition.script:
main();
function main() {
  const parentFolderId = configs.get("Conf_ParentFolderId");
  const fileName = configs.get("Conf_FileName");
  if (fileName == "" || fileName == null) {
    throw "File Name is blank";
  }
  const sheetTitle = configs.get("Conf_SheetTitle");
  if (sheetTitle == "" || sheetTitle == null) {
    throw "Sheet Title is blank";
  }
  if (sheetTitle.length > 100) {
    throw "Sheet Title should be 100 characters or less";
  }
  const userId = configs.get("Conf_UserID");
  const quser = quserDao.findById(parseInt(userId));
  if (quser == null) {
    throw "User not found:" + userId;
  }
  engine.log("User Name:" + quser.getName());

  // get OAuth token
  let token;
  try{
    token = httpClient.getGoogleOAuth2Token(quser, "Drive");
  }catch(e){
    throw "This User has not connected with Google Drive."
  }

  const json = createFile(token, parentFolderId, fileName);

  // get spreadsheetId
  const fileId = json["id"];

  updateSheetTitle(token, fileId, sheetTitle);

  setData("Conf_FileIdItem", json, "id");
  setData("Conf_WebViewUrlItem", json, "webViewLink");
}

// save Data to process data
function setData(configName, json, property) {
  const item = configs.get(configName);
  if (item == null || item == "") {
    return;
  }
  const data = json[property];
  engine.setDataByNumber(item, data);
}

// create spreadsheet on google drive
function createFile(token, parentFolderId, fileName) {
  let jsonRes = {};
  jsonRes["mimeType"] = "application/vnd.google-apps.spreadsheet";
  if (parentFolderId !== "" && parentFolderId !== null){
    jsonRes["parents"] = [ parentFolderId + '' ];
  }
  jsonRes["name"] = fileName + '';

  const url = 'https://www.googleapis.com/drive/v3/files/';
  const response = httpClient.begin()
    .bearer(token)
    .queryParam("fields", "id, webViewLink")
    .queryParam("supportsAllDrives", "true")
    .body(JSON.stringify(jsonRes), "application/json; charset=UTF-8")
    .post(url);
  const status = response.getStatusCode();
  const responseTxt = response.getResponseAsString();
  if (status >= 300) {
    const error = "Failed to create \n status:" + status + "\n" + responseTxt
    throw error;
  }
  engine.log("status of file create:" + status);
  engine.log(responseTxt);

  jsonRes = JSON.parse(responseTxt);
  return jsonRes;
}

// update sheet title on google drive
function updateSheetTitle(token, fileId, sheetTitle) {

  let jsonRes = {};
  jsonRes.requests = [];
  jsonRes.requests[0] = {};
  jsonRes.requests[0].updateSheetProperties = {};
  jsonRes.requests[0].updateSheetProperties.fields = "title";
  jsonRes.requests[0].updateSheetProperties.properties = {};
  jsonRes.requests[0].updateSheetProperties.properties.sheetId = 0;
  jsonRes.requests[0].updateSheetProperties.properties.title = sheetTitle;

  const url = "https://sheets.googleapis.com/v4/spreadsheets/"+ fileId + ":batchUpdate";
  const response = httpClient.begin()
    .bearer(token)
    .body(JSON.stringify(jsonRes), "application/json")
    .post(url);
  const status = response.getStatusCode();
  const responseTxt = response.getResponseAsString();
  if (status >= 300) {
    const error = "Failed to update sheet title \n status:" + status + "\n" + responseTxt
    throw error;
  }
  engine.log("status of sheet title update:" + status);
  engine.log(responseTxt);

  jsonRes = JSON.parse(responseTxt);
}
