service-task-definition.script:
// OAuth2 config
// Authorization Endpoint URL: https://accounts.google.com/o/oauth2/auth?access_type=offline&approval_prompt=force
// Token Endpoint URL: https://accounts.google.com/o/oauth2/token
// Scope: https://www.googleapis.com/auth/spreadsheets
// Consumer Key: (Get by Google Developers Console)
// Consumer Secret: (Get by Google Developers Console)

main();
function main(){
  //// == 工程コンフィグの参照 / Config Retrieving ==
  const oauth2 = configs.get("conf_OAuth2");

  const spreadSheetId = configs.get("conf_SpreadSheetId");
  if (spreadSheetId === "" || spreadSheetId === null) {
    throw "Source SpreadSheet ID isn't set";
  }

  const sheetTitle = configs.get("conf_SheetTitle");
  if (sheetTitle === "" || sheetTitle === null) {
    throw "Source SpreadSheet Title isn't set";
  }

  const destinationDef = configs.getObject("conf_Destination");
  let destination = "";
  if (destinationDef === null) {
    destination = configs.get("conf_Destination");
  } else {
    destination = engine.findData(destinationDef);
  }

  if (destination === "" || destination === null) {
    destination = spreadSheetId;
  }

  const newSheetTitle = configs.get("conf_NewSheetTitle");
  if (100 <= newSheetTitle.length) {
    throw "Sheet Title is too long.";
  }

  //// == Calculating / 演算 ==
  const sheetId = getSheetId(oauth2, spreadSheetId, sheetTitle);

  copySheet(oauth2, spreadSheetId, sheetId, destination);

  if (newSheetTitle !== "" && newSheetTitle !== null) {
    setTitle(oauth2, spreadSheetId, sheetId, newSheetTitle);
  }
}

/**
 * Google スプレッドシートのシート ID を取得
 * @param {String} oauth2 OAuth2 認証設定
 * @param {String} spreadSheetId スプレッドシートの ID
 * @param {String} sheetTitle シートタイトル
 * @return {Number} sheetId シート ID
 */
function getSheetId(oauth2, spreadSheetId, sheetTitle){
  const uri = `https://sheets.googleapis.com/v4/spreadsheets/${encodeURIComponent(spreadSheetId)}?includeGridData=false`;
  const response = httpClient.begin()
    .authSetting(oauth2)
    .get(uri);
  const status = response.getStatusCode();
  const responseStr = response.getResponseAsString();
  if (status !== 200) {
    engine.log(responseStr);
    throw `Failed to get sheet information. status: ${status}`;
  }
  const jsonObj = JSON.parse(responseStr);
  engine.log(`SPREADSHEET (TITLE: ${jsonObj.properties.title}) has ${jsonObj.sheets.length} sheets`);
  for (let i = 0; i < jsonObj.sheets.length; i++) {
    if (jsonObj.sheets[i].properties.title === sheetTitle) {
      const sheetId = parseInt(jsonObj.sheets[i].properties.sheetId, 10);
      engine.log(`SHEET TITLE: ${sheetTitle} exists (id: ${sheetId})`);
      return sheetId;
    }
  }
  // error (not exist)
  throw `Sheet ${sheetTitle} does not exist`;
}


/**
 * Google スプレッドシートに新しいシートを追加
 * @param {String} oauth2 OAuth2 認証設定
 * @param {String} spreadSheetId スプレッドシートの ID
 * @param {String} sheetId シートの ID
 * @param {String} destination コピー先スプレッドシート
 */
function copySheet(oauth2, spreadSheetId, sheetId, destination) {
  // Method: spreadsheets.copyTo
  const uri = `https://sheets.googleapis.com/v4/spreadsheets/${spreadSheetId}/sheets/${sheetId}:copyTo`;

  const requestObj = {};
  requestObj.destinationSpreadsheetId = destination;

  const response = httpClient.begin()
    .authSetting(oauth2)
    .body(JSON.stringify(requestObj), "application/json")
    .post(uri);

  const status = response.getStatusCode();
  const responseStr = response.getResponseAsString();
  if (status >= 300) {
    engine.log(`Status: ${status}`);
    engine.log(responseStr);
    throw `Failed to copy sheet.`;
  }
  engine.log("Succeeded to copy sheet");
}


/**
 * Google スプレッドシートのシート名を変更する
 * @param {String} oauth2 OAuth2 認証設定
 * @param {String} spreadSheetId コピー先スプレッドシートの ID
 * @param {String} sheetId コピー先シートの ID
 * @param {String} newSheetTitle シートの新しいタイトル
 */
function setTitle(oauth2, spreadSheetId, sheetId, newSheetTitle) {
  // Method: spreadsheets.batchUpdate
  const uri = `https://sheets.googleapis.com/v4/spreadsheets/${encodeURIComponent(spreadSheetId)}:batchUpdate`;

  const requestObj = {
    requests: []
  };

  requestObj.requests[0] = {
    updateSheetProperties: {
      fields: 'title',
      properties: {
        "title": newSheetTitle,
        "sheetId": sheetId
      }
    }
  }

  const response = httpClient.begin()
    .authSetting(oauth2)
    .body(JSON.stringify(requestObj), "application/json")
    .post(uri);

  const status = response.getStatusCode();
  const responseStr = response.getResponseAsString();
  if (status >= 300) {
    engine.log(`Status: ${status}`);
    engine.log(responseStr);
    throw `Failed to set sheet title.`;
  }
  engine.log("Succeeded to set sheet title");
}
