    execute();
    fail("not come here");
  } catch (e) {
    expect(e.message).endsWith(
      "Failed to get sheet information. status: ${status}"
    );
  }
});

/**
 * シートコピー成功の場合 - 新しいシートのタイトルを指定
 */
test("Success - Copy Sheet Title", () => {
  const addedSheetTitleDef = prepareConfigs("abc123", "新しいシート / 1");
  httpClient.setRequestHandler((request) => {
    assertPostRequest(request, "abc123", "新しいシート / 1");
    return httpClient.createHttpResponse(
      200,
      "application/json",
      JSON.stringify(preparePostResponse("新しいシート / 1"))
    );
  });
  // <script> のスクリプトを実行
  execute();
  // 文字型データ項目の値をチェック
  expect(engine.findData(addedSheetTitleDef)).toEqual("新しいシート / 1");
});

/**
 * シートコピー成功の場合 - 新しいシートのタイトルは自動設定
 */
test("Success - without New Sheet Title", () => {
  const addedSheetTitleDef = prepareConfigs("abc123", "");
  httpClient.setRequestHandler((request) => {
    assertPostRequest(request, "abc123", "");
    return httpClient.createHttpResponse(
      200,
      "application/json",
      JSON.stringify(preparePostResponse("シート2"))
    );
  });
  // <script> のスクリプトを実行
  execute();
  // 文字型データ項目の値をチェック
  expect(engine.findData(addedSheetTitleDef)).toEqual("シート2");
});

/**
 * シートコピー成功の場合 - スプレッドシートの ID を固定値で指定
 */
test("Success - Set the Spreadsheet ID as a fixed value", () => {
  configs.put("conf_OAuth2", "Google");
  configs.put("conf_SpreadSheetId", "abc123");
  configs.put("conf_SheetTitle", "新しいシート");
  // コピーしたシートのタイトルを保存する文字型データ項目（単一行）を準備
  const addedSheetTitleDef = engine.createDataDefinition(
    "コピーしたシートのタイトル",
    1,
    "q_AddedSheetTitle",
    "STRING_TEXTFIELD"
  );
  engine.setData(addedSheetTitleDef, "事前文字列");
  configs.putObject("conf_AddedSheetTitle", addedSheetTitleDef);
  httpClient.setRequestHandler((request) => {
    assertPostRequest(request, "abc123", "新しいシート");
    return httpClient.createHttpResponse(
      200,
      "application/json",
      JSON.stringify(preparePostResponse("新しいシート"))
    );
  });
  // <script> のスクリプトを実行
  execute();
  // 文字型データ項目の値をチェック
  expect(engine.findData(addedSheetTitleDef)).toEqual("新しいシート");
});

/**
 * シートコピー成功の場合 - コピーしたシートのタイトルを保存しない
