  const files = new java.util.ArrayList();
  files.add(createQfile(`file1.txt`, 1));
  prepareConfigs(configs, '', 'invalidDomain', '', '1', '1', "添付ファイル", files );


  // <script> のスクリプトを実行し、エラーがスローされることを確認
