/**
 * GET リクエストのテスト
 * @param {Object} request
 * @param request.url
 * @param request.method
 * @param domain
 * @param guestSpaceId
 * @param appId
 * @param recordId
 */
const assertGetRequest = ({url, method, body}, domain, guestSpaceId, appId, recordId) => {
  expect(method).toEqual('GET');

  if ( guestSpaceId === "" || guestSpaceId === null ) {
    expect(url).toEqual(`https://${domain}/k/v1/record.json?app=${appId}&id=${recordId}`);
  } else {
    expect(url).toEqual(`https://${domain}/k/guest/${guestSpaceId}/v1/record.json?app=${appId}&id=${recordId}`);
  }
};



/**
 * POST リクエストのテスト
 * @param {Object} request
 * @param request.url
 * @param request.method
 * @param domain
 * @param guestSpaceId
 * @param appId
 * @param recordId
 */
const assertPostRequest = ({url, method, body}, domain, guestSpaceId, appId, recordId, files, filename, filesize) => {
  expect(method).toEqual('POST');

  if ( guestSpaceId === "" || guestSpaceId === null ) {
    expect(url).toEqual(`https://${domain}/k/v1/file.json`);
  } else {
    expect(url).toEqual(`https://${domain}/k/guest/${guestSpaceId}/v1/file.json`);
  }

  expect(body.size()).toEqual(1);

  const file = body.get('file').get(0);
  expect(file.getName()).toEqual(filename);
  expect(file.getContentType()).startsWith('text/plain');
  expect(file.getLength()).toEqual(filesize);

};



/**
 * PUT リクエストのテスト
 * @param {Object} request
 * @param request.url
 * @param request.method
 * @param domain
 * @param guestSpaceId
 * @param appId
 * @param recordId
 */
const assertPutRequest = ({url, method, body}, domain, guestSpaceId, appId, recordId, attachments, attachFileFieldCode) => {
  expect(method).toEqual('PUT');

  if ( guestSpaceId === "" || guestSpaceId === null ) {
    expect(url).toEqual(`https://${domain}/k/v1/record.json`);
  } else {
    expect(url).toEqual(`https://${domain}/k/guest/${guestSpaceId}/v1/record.json`);
  }
  engine.log("PUTリクエスト");
  engine.log("body");
  engine.log(body);
  engine.log("attachments");
  engine.log(JSON.stringify(attachments));
  const bodyObj = JSON.parse(body);
  expect(bodyObj.app).toEqual(appId);
  expect(bodyObj.id).toEqual(recordId);
  expect(bodyObj.record[attachFileFieldCode].value).toEqual(attachments);
};



/**
 * GET リクエストのレスポンスを準備
 * @param attachFileFieldCode
 * @param num 添付されているファイル数
 * @return responseObj
 */
const prepareGetResponse = (attachFileFieldCode, num) => {

  const responseObj = {
    "record": {
      [attachFileFieldCode]: {
        "type": "FILE",
        "value": [
        ]
      }
    }
  };

  if (num !== 0){
    for (let i =1; i <= num ; i ++){
      responseObj.record[attachFileFieldCode].value.push({
            "contentType": "text/plain",
            "fileKey":`key-${i}`,
            "name": `test${i}.txt`,
            "size": "23175"
      })
    }
  }

  return responseObj;
};



/**
 * POST リクエストのレスポンスを準備
 * @return responseObj
 */
const preparePostResponse = (fileKey) => {
  return {
      fileKey
  };
};



/**
 * PUT リクエストのレスポンスを準備
 * @param app
 * @param id
 * @param attachFileFieldCode
 * @param num 添付されているファイル数
 * @return responseObj
 */
const preparePutResponse = (app, id, attachFileFieldCode, num) => {

  const responseObj = {
    app,
    id,
    "record": {
      [attachFileFieldCode]: {
        "type": "FILE",
        "value": [
        ]
      }
    }
  };

  if (num !== 0){
    for (let i =1; i <= num ; i ++){
      responseObj.record[attachFileFieldCode].value.push({
            "contentType": "text/plain",
            "fileKey":`key-${i}`,
            "name": `test${i}.txt`,
            "size": "23175"
      })
    }
  }

  return responseObj;
};


