    httpClient.setRequestHandler((request) => {
        assertGetRequest(request, domain, '', appId, recordId );
        return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(prepareGetResponse(attachFileFieldCode, 0)));
    });

    expect(execute).toThrow(`Necessary HTTP requests exceeds the limit.`);
});



/**
 * POST API リクエストでエラーになる場合 
 */
test('POST Failed', () => {
    const files = new java.util.ArrayList();
    const fileName = 'file5.txt';
    const fileSize = 5;
    files.add(createQfile(fileName, fileSize));

    const domain = 'test.kintone.com';
    const appId = '5';
    const recordId = '5';

    prepareConfigs(configs, '', domain, '', appId, recordId, "添付ファイル", files);

    let reqCount = 0;
    const attachFileFieldCode = "添付ファイル";

    httpClient.setRequestHandler((request) => {
        if (reqCount === 0) {
            assertGetRequest(request, domain, '', appId, recordId );
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(prepareGetResponse(attachFileFieldCode, 0)));
        }
        assertPostRequest(request, domain, '', appId, recordId, files, fileName, fileSize);
        return httpClient.createHttpResponse(400, 'application/json', '{}');
    });

    expect(execute).toThrow(`Failed to upload file. status: 400`);
});



/**
 * PUT API リクエストでエラーになる場合 
 */
test('PUT Failed', () => {
    const files = new java.util.ArrayList();
    const fileName = 'file6.txt';
    const fileSize = 6;
    files.add(createQfile(fileName, fileSize));

    const domain = 'test.kintone.com';
    const appId = '6';
    const recordId = '6';

    prepareConfigs(configs, '', domain, '', appId, recordId, "添付ファイル", files);

    let reqCount = 0;
    const attachFileFieldCode = "添付ファイル";
    let attachments;
    let postResponseObj;

    httpClient.setRequestHandler((request) => {

        if (reqCount === 0) {
            assertGetRequest(request, domain, '', appId, recordId );
            reqCount++;
            attachments = prepareGetResponse(attachFileFieldCode, 0);
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(attachments));
        }

        if (reqCount === 1) {
            assertPostRequest(request, domain, '', appId, recordId, files, fileName, fileSize);
            reqCount++;
            postResponseObj = preparePostResponse('fileKey-01234-56789')
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(postResponseObj));
        }

        attachments.record[attachFileFieldCode].value.push(postResponseObj);
        valueAttachments = attachments.record[attachFileFieldCode].value;
        assertPutRequest(request, domain, '', appId, recordId, valueAttachments, attachFileFieldCode);
        return httpClient.createHttpResponse(400, 'application/json', '{}');
    });

    expect(execute).toThrow(`Failed to update record. status: 400`);
});



/**
 * ファイルアップロード成功
 * basic 認証設定　ゲストスペースID 設定
 * ファイルが添付されていないフィールドコードにアップロードする
 */
test('Sucsess - basic and guestSpaseId', () => {
    const files = new java.util.ArrayList();
    const fileName = 'file7.txt';
    const fileSize = 7;
    files.add(createQfile(fileName, fileSize));

    const basic = 'basic';
    const guestSpaceId = '7';
    const domain = 'test.cybozu.com';
    const appId = '7';
    const recordId = '7';

    prepareConfigs(configs, basic, domain, guestSpaceId, appId, recordId, "添付ファイル", files);

    let reqCount = 0;
    const attachFileFieldCode = "添付ファイル";
    let attachments;
    let postResponseObj;

    httpClient.setRequestHandler((request) => {
        if (reqCount === 0) {
            assertGetRequest(request, domain, guestSpaceId, appId, recordId );
            reqCount++;
            attachments = prepareGetResponse(attachFileFieldCode, 0);
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(attachments));
        }
        if (reqCount === 1) {
            assertPostRequest(request, domain, guestSpaceId, appId, recordId, files, fileName, fileSize);
            reqCount++;
            postResponseObj = preparePostResponse('fileKey-11111-22222');
            attachments.record[attachFileFieldCode].value.push(postResponseObj);
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(postResponseObj));
        }

        engine.log("ファイルアップロード成功1")
        engine.log(JSON.stringify(attachments));        
        valueAttachments = attachments.record[attachFileFieldCode].value;
        assertPutRequest(request, domain, guestSpaceId, appId, recordId, valueAttachments, attachFileFieldCode);

        const putResponseObj = preparePutResponse(appId, recordId, attachFileFieldCode, 0);
        engine.log("ファイルアップロード成功1-2")
        engine.log(JSON.stringify(putResponseObj));
        putResponseObj.record[attachFileFieldCode].value.push({"fileKey":"fileKey-11111-22222"});
        engine.log("ファイルアップロード成功1-3")
        engine.log(JSON.stringify(putResponseObj));
        return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(putResponseObj));
    });

    // <script> のスクリプトを実行
    execute();
});



/**
 * ファイルアップロード成功
 * 複数ファイル（ファイル最大値）
 * 既に 2 ファイル添付されているフィールドコードにアップロードする
 */
test('Sucsess - Max-files', () => {

    const files = new java.util.ArrayList();
    for (let i = 0; i < httpClient.getRequestingLimit() - 2; i++) {
        files.add(createQfile(`ファイル${i + 1}.txt`, 1 ));
        engine.log(`ファイル${i + 1}.txt`);
    }

    const basic = '';
    const guestSpaceId = '';
    const domain = 'test.cybozu.com';
    const appId = '8';
    const recordId = '8';

    prepareConfigs(configs, basic, domain, guestSpaceId, appId, recordId, "添付ファイル", files);

    let reqCount = 0;
    const attachFileFieldCode = "添付ファイル";
    let attachments;
    let postResponseObj;

    httpClient.setRequestHandler((request) => {

        engine.log("ファイルアップロード成功2")
        engine.log(JSON.stringify(attachments));

        if (reqCount === 0) {
            assertGetRequest(request, domain, guestSpaceId, appId, recordId );
            attachments = prepareGetResponse(attachFileFieldCode, 2);
            engine.log("ファイルアップロード成功2-1")
            engine.log(JSON.stringify(attachments));
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(attachments));
        }

        if (reqCount <= httpClient.getRequestingLimit() - 2) {
            let fileName = `ファイル${reqCount}.txt`;
            engine.log("複数ファイル（ファイル最大値）");
            engine.log(fileName);
            const fileSize = 1;
            assertPostRequest(request, domain, guestSpaceId, appId, recordId, files, fileName, fileSize);
            const fileKey = `fileKey-${reqCount}-01234-56789`;
            postResponseObj = preparePostResponse(fileKey);
            attachments.record[attachFileFieldCode].value.push(postResponseObj);

            engine.log("ファイルアップロード成功2-2");
            engine.log(JSON.stringify(attachments));
            reqCount++

            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(postResponseObj));			
        }

        valueAttachments = attachments.record[attachFileFieldCode].value;
        assertPutRequest(request, domain, guestSpaceId, appId, recordId, valueAttachments, attachFileFieldCode);

        const putResponseObj = preparePutResponse(appId, recordId, attachFileFieldCode, 2);

        for (let j = 1; j < httpClient.getRequestingLimit() - 2; j++) {
            const fileKeyPut = `fileKey-${j}-01234-56789`;
            putResponseObj.record[attachFileFieldCode].value.push({fileKeyPut});
        }

        engine.log("ファイルアップロード成功2-3")
        engine.log(JSON.stringify(putResponseObj));
        return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(putResponseObj));
    });

    // <script> のスクリプトを実行
    execute();
});
