  configs.put(`conf_fieldCode3`, `フィールドコード_3`);
  configs.put(`conf_fieldValue3`, 'test3');

  // <script> のスクリプトを実行し、エラーがスローされることを確認
  expect(execute).toThrow('Invalid App ID.');
});


