service-task-definition.script:
const MAX_EMAIL_LENGTH = 254;
const MAX_NAME_LENGTH = 50;
const MAX_POSTAL_CODE_LENGTH = 60; // API ドキュメントに記載がないが、試したところ 60 文字だった
const MAX_CITY_LENGTH = 60;
const MAX_ADDRESS_LENGTH = 1000;
const MAX_ALTERNATE_EMAIL_NUM = 5;

function main(){
    //// == Config Retrieving / 工程コンフィグの参照 ==
    const auth = configs.get('conf_Auth');
    const listIds = retrieveListIds();
    const contact = retrieveContact();
    const customFieldMap = retrieveCustomFieldsAsMap();

    //// == Calculating / 演算 ==
    checkAlreadyExists(auth, contact.email);
    if (customFieldMap.size > 0) {
        const allCustomFields = getAllCustomFields(auth);
        setCustomFields(contact, customFieldMap, allCustomFields);
    }
    const jobId = addContact(auth, listIds, contact);

    // 処理状況を確認し、未完了なら proceed() に進む
    if (checkStatus(auth, jobId) === false) {
        engine.saveTemporaryData(jobId);
        return false;
    }
}

function proceed() {
    //// == Config Retrieving / 工程コンフィグの参照 ==
    const auth = configs.get('conf_Auth');
    const email = configs.get('conf_Email');

    //// == Restoring Temporary Data / 一時データの読み出し ==
    const jobId = engine.restoreTemporaryData();
    if (jobId === null) {
        throw 'Temporary data has not been saved.';
    }

    //// == 演算 / Calculating ==
    if (checkStatus(auth, jobId) === false) {
        return false;
    }
}

/**
  * config に設定されたリスト ID 一覧を読み出す
  * @return {Array<String>} listIds
  */
function retrieveListIds() {
    const listIds = configs.get('conf_ListIds').split('\n')
        .filter(id => id !== '');
    // API ドキュメントに List ID 上限数の記載なしのため、上限数はチェックしない
    return listIds;
}

/**
  * config に設定された宛先情報を、API リクエストボディに設定する形式で読み出す
  * この時点では、カスタムフィールドの情報は含まない
  * @return {Object} contact
  * @return {String} contact.email
  * @return {Array<String>} contact.alternate_emails
  * @return {String} contact.last_name
  * @return {String} contact.first_name
  * @return {String} contact.country
  * @return {String} contact.postal_code
  * @return {String} contact.state_province_region
  * @return {String} contact.city
  * @return {String} contact.address_line_1
  * @return {String} contact.address_line_2
  */
function retrieveContact() {
    const email = retrieveConfig('conf_Email', true, MAX_EMAIL_LENGTH, 'Email');
    const alternateEmails = retrieveAlternateEmails();
    const lastName = retrieveConfig('conf_LastName', false, MAX_NAME_LENGTH, 'Last Name');
    const firstName = retrieveConfig('conf_FirstName', false, MAX_NAME_LENGTH, 'First Name');
    const country = retrieveConfig('conf_Country', false, MAX_NAME_LENGTH, 'Country');
    const postalCode = retrieveConfig('conf_PostalCode', false, MAX_POSTAL_CODE_LENGTH, 'Postal Code');
    const region = retrieveConfig('conf_Region', false, MAX_NAME_LENGTH, 'Region');
    const city = retrieveConfig('conf_City', false, MAX_CITY_LENGTH, 'City');
    const address1 = retrieveConfig('conf_Address1', false, MAX_ADDRESS_LENGTH, 'Address 1st Line');
    const address2 = retrieveConfig('conf_Address2', false, MAX_ADDRESS_LENGTH, 'Address 2nd Line');

    const contact = {
        email,
        alternate_emails: alternateEmails,
        last_name: lastName,
        first_name: firstName,
        country,
        postal_code: postalCode,
        state_province_region: region,
        city,
        address_line_1: address1,
        address_line_2: address2
    };

    return contact;
}

/**
  * config に設定された文字列を読み出す
  * @param {String} confName 設定項目名
  * @param {boolean} required 必須項目かどうか
  * @param {Number} maxLength 最大文字数
  * @param {String} label エラー出力用ラベル
  * @return {String} value
  */
function retrieveConfig(confName, required, maxLength, label) {
    const value = configs.get(confName);
    if (required && value === '') {
        throw `${label} is blank.`;
    }
    if (value.length > maxLength) {
        trow `${label} must be within ${maxLength} characters.`;
    }
    return value;
}

/**
  * config に設定された代替メールアドレスの一覧を読み出す
  * @return {Array<String>} alternateEmails
  */
function retrieveAlternateEmails() {
    const alternateEmails = configs.get('conf_AlternateEmails').split('\n')
        .filter(email => email !== '');
    if (alternateEmails.length > MAX_ALTERNATE_EMAIL_NUM) {
        throw `The maximum number of alternate emails is ${MAX_ALTERNATE_EMAIL_NUM}.`;
    }
    // API ドキュメントに上限文字数の記載はないが、メインのメールアドレスと同様にチェック
    if (alternateEmails.some(email => email.length > MAX_EMAIL_LENGTH)) {
        throw `Each alternate email must be within ${MAX_EMAIL_LENGTH} characters.`;
    }
    return alternateEmails;
}

/**
  * config に設定されたカスタムフィールドを読み出す
  * Map のキーはサブデータの定義、値はセルデータのオブジェクト
  * @return {Map<SubDataDefinitionView, Object>} customFieldMap
  */
function retrieveCustomFieldsAsMap() {
    const customFieldMap = new Map();
    const tableDef = configs.getObject('conf_CustomFields');
    if (tableDef === null) {
        return customFieldMap;
    }
    const table = engine.findData(tableDef); // ScriptListArray
    if (table === null || table.size() === 0) {
        return customFieldMap;
    }
    if (table.size() > 1) {
        throw 'Custom Fields must be set by a table with a single row.';
    }

    const subDataDefs = tableDef.getSubDataDefinitions(); // List<SubDataDefinitionView>
    for (let i = 0; i < subDataDefs.size(); i++) {
        const subDataDef = subDataDefs.get(i);
        const dataObj = table.getObject(0, i);
        if (dataObj !== null && dataObj !== '') {
            // 値が空でない場合のみ、Map に追加
            customFieldMap.set(subDataDef, dataObj);
        }
    }
    return customFieldMap;
}

/**
  * 指定したメールアドレスの宛先が既に存在するかどうかをチェックする
  * 既に存在する場合はエラー
  * @param {String} auth 認証設定名
  * @param {String} email メールアドレス
  */
function checkAlreadyExists(auth, email) {
    const lowerCaseEmail = email.toLowerCase();
    const requestBody = {
        query: `email = "${lowerCaseEmail}"`
    };
    const response = httpClient.begin()
        .authSetting(auth)
        .body(JSON.stringify(requestBody), 'application/json')
        .post('https://api.sendgrid.com/v3/marketing/contacts/search');

    const status = response.getStatusCode();
    const responseStr = response.getResponseAsString();
    if (status !== 200) {
        engine.log(responseStr);
        throw `Failed to search contact. status: ${status}`;
    }
    const contacts = JSON.parse(responseStr).result;
    if (contacts.length > 0) {
        throw `Contact with email "${lowerCaseEmail}" already exists.`;
    }
}

/**
  * 全カスタムフィールドの一覧を取得する
  * @param {String} auth 認証設定名
  * @return {Array<Object>} allCustomFields
  */
function getAllCustomFields(auth) {
    const response = httpClient.begin()
        .authSetting(auth)
        .get('https://api.sendgrid.com/v3/marketing/field_definitions');

    const status = response.getStatusCode();
    const responseStr = response.getResponseAsString();
    if (status !== 200) {
        engine.log(responseStr);
        throw `Failed to get field definitions. status: ${status}`;
    }
    return JSON.parse(responseStr).custom_fields;
}

/**
  * 宛先オブジェクトにカスタムフィールドの情報を追加する
  * @param {Object} contact 宛先オブジェクト
  * @param {Map<SubDataDefinitionView, Object>} customFieldMap カスタムフィールドの Map
  * @param {Array<Object>} allCustomFields 全カスタムフィールドの一覧
  */
function setCustomFields(contact, customFieldMap, allCustomFields) {
    const customFields = {};
    customFieldMap.forEach((dataObj, subDataDef) => {
        const fieldName = subDataDef.getName();
        const customField = allCustomFields.find(field => field.name === fieldName);
        if (customField === undefined) { // 指定したフィールド名のカスタムフィールドがない場合
            throw `Custom Field named "${fieldName}" does not exist.`;
        }
        switch (customField.field_type) {
            case 'Text':
                addTextField(customFields, customField.id, subDataDef, dataObj);
                break;
            case 'Number':
                addNumberField(customFields, customField.id, subDataDef, dataObj);
                break;
            case 'Date':
                addDateField(customFields, customField.id, subDataDef, dataObj);
                break;
            default:
                // ありえないが、念のため
                throw `Custom Field type "${customField.field_type}" is not supported.`;
        }
    });
    Object.assign(contact, {custom_fields: customFields});
}

function addTextField(customFields, customFieldId, subDataDef, dataObj) {
    if (subDataDef.matchDataType('STRING')) {
        // dataObj は String
        Object.assign(customFields, {[customFieldId]: dataObj});
        return;
    }
    if (subDataDef.matchDataType('SELECT')) {
        // TODO 後で実装
        return;
    }
    throw `Custom Field "${subDataDef.getName()}" is Text-type but the table sub-data is neither STRING nor SELECT.`;
}

function addNumberField(customFields, customFieldId, subDataDef, dataObj) {
    if (!subDataDef.matchDataType('DECIMAL')) {
        throw `Custom Field "${subDataDef.getName()}" is Number-type but the table sub-data is not DECIMAL.`;
    }
    // dataObj は BigDecimal
    const fieldValue = dataObj.doubleValue();
    Object.assign(customFields, {[customFieldId]: fieldValue});
}

function addDateField(customFields, customFieldId, subDataDef, dataObj) {
    if (!subDataDef.matchDataType('DATE')) {
        throw `Custom Field "${subDataDef.getName()}" is Date-type but the table sub-data is not DATE.`;
    }
    // dataObj は AddableDate
    const fieldValue = dataObj.toString(); // yyyy-MM-dd
    Object.assign(customFields, {[customFieldId]: fieldValue});
}

/**
  * 宛先を追加する
  * @param {String} auth 認証設定名
  * @param {Array<String>} listIds 宛先を追加するリスト ID の一覧
  * @param {Object} contact 宛先情報
  * @return {String} jobId
  */
function addContact(auth, listIds, contact) {
    const requestBody = {
        "list_ids": listIds,
        "contacts": [contact]
    };
    const response = httpClient.begin()
        .authSetting(auth)
        .body(JSON.stringify(requestBody), 'application/json')
        .put('https://api.sendgrid.com/v3/marketing/contacts');

    const status = response.getStatusCode();
    const responseStr = response.getResponseAsString();
    if (status !== 202) {
        engine.log(responseStr);
        throw `Failed to add contact. status: ${status}`;
    }
    return JSON.parse(responseStr).job_id;
}

function checkStatus(auth, jobId) {
    const response = httpClient.begin()
        .authSetting(auth)
        .get(`https://api.sendgrid.com/v3/marketing/contacts/imports/${jobId}`);

    const status = response.getStatusCode();
    const responseStr = response.getResponseAsString();
    if (status !== 200) {
        engine.log(responseStr);
        throw `Failed to get import status. status: ${status}`;
    }
    const jobStatus = JSON.parse(responseStr).status;
    switch (jobStatus) {
        case 'pending': // 処理中
            return false;
        case 'completed': // 完了
            return true;
        default: // errored or failed
            engine.log(responseStr);
            throw 'Failed to complete adding the contact.';
    }
}
