service-task-definition.script:
main();
function main(){
  const toAddress = configs.get("ToAddress");
  const tempId = configs.get("Template");
  const fromAddress = configs.get("FromAddress");
  const dynamicTemp = engine.findDataByNumber(configs.get("DynamicTemp"));

  const json = {
    "personalizations": [
      {
        "to": [{}],
        "dynamic_template_data": {},
      }
    ],
    "from": {},
  };
  json["personalizations"][0]["to"][0]["email"] = toAddress;
  json["template_id"] = tempId;
  json["from"]["email"] = fromAddress;
  for(i=0;i<dynamicTemp.size();i++){
    json["personalizations"][0]["dynamic_template_data"][dynamicTemp.get(i,0)] = dynamicTemp.get(i,1);
  }

 const response = httpClient.begin()
    .bearer(configs.get("Key"))
    .body(JSON.stringify(json),"application/json; charset=UTF-8")
    .post("https://api.sendgrid.com/v3/mail/send");

  engine.log(response.getStatusCode());
  engine.log(response.getResponseAsString());
}
