test('Open invoice: Success - sendReceipt is false', () => {
    prepareConfigs('in_00044', false);

    const invoiceResStr = invoiceRes('open', 'cus_00004', 'pi_00004', true);
    let reqCount = 0;
    httpClient.setRequestHandler((request) => {
        if (reqCount === 0) {
            assertGetInvoiceRequest(request, 'in_00044');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', invoiceResStr);
        }
        if (reqCount === 1) {
            assertUpdateInvoiceRequest(request, 'in_00044');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', '{}');
        }
        if (reqCount === 2) {
            assertGetCustomerRequest(request, 'cus_00004');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_CUSTOMER_1));
        }
        if (reqCount === 3) {
            assertUpdatePaymentIntentRequest(request, 'pi_00004', ''); // 領収書送付先メールアドレスが空に設定される
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', '{}');
        }
        assertSendInvoiceRequest(request, 'in_00044');
        return httpClient.createHttpResponse(200, 'application/json', '{}');
    });

    execute();
});


