/**
 * 支払オブジェクトを取得する API リクエストのテスト
 * @param {Object} request
 * @param request.url
 * @param request.method
 * @param customerId
 */
const assertGetPaymentIntentRequest = ({url, method}, paymentIntentId) => {
    expect(url).toEqual(`https://api.stripe.com/v1/payment_intents/${paymentIntentId}`);
    expect(method).toEqual('GET');
};

/**
 * 支払オブジェクトを取得する HTTP リクエストで失敗
 */
test('Uncollectible invoice: Fail to retrieve payment intent', () => {
    prepareConfigs('in_00101', true);

    const invoiceResStr = invoiceRes('uncollectible', 'cus_00001', 'pi_00001', true);
    let reqCount = 0;
    httpClient.setRequestHandler((request) => {
        if (reqCount === 0) {
            assertGetInvoiceRequest(request, 'in_00101');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', invoiceResStr);
        }
        assertGetPaymentIntentRequest(request, 'pi_00001');
        return httpClient.createHttpResponse(400, 'application/json', '{}');
    });

    expect(execute).toThrow('Failed to retrieve payment intent. status: 400');
});

const SAMPLE_PAYMENT_INTENT_OK = {
    'payment_method_types': ['card']
};

const SAMPLE_PAYMENT_INTENT_NG = {
    'payment_method_types': ['card', 'wechat_pay']
};

/**
 * 支払方法の種別に card 以外が含まれている
 */
test('Uncollectible invoice: Includes payment method type other than card', () => {
    prepareConfigs('in_00102', true);

    const invoiceResStr = invoiceRes('uncollectible', 'cus_00002', 'pi_00002', false);
    let reqCount = 0;
    httpClient.setRequestHandler((request) => {
        if (reqCount === 0) {
            assertGetInvoiceRequest(request, 'in_00102');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', invoiceResStr);
        }
        assertGetPaymentIntentRequest(request, 'pi_00002');
        return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_PAYMENT_INTENT_NG));
    });

    expect(execute)
        .toThrow('Payment method types includes types other than card, and uncollectible invoice cannot be updated.');
});

/**
 * 請求書の支払方法が send_invoice でない
 */
test('Uncollectible invoice: Invalid collection method', () => {
    prepareConfigs('in_00103', true);

    const invoiceResStr = invoiceRes('uncollectible', 'cus_00003', 'pi_00003', false);
    let reqCount = 0;
    httpClient.setRequestHandler((request) => {
        if (reqCount === 0) {
            assertGetInvoiceRequest(request, 'in_00103');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', invoiceResStr);
        }
        assertGetPaymentIntentRequest(request, 'pi_00003');
        return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_PAYMENT_INTENT_OK));
    });

    expect(execute).toThrow('The collection method of the invoice is not send_invoice.');
});

/**
 * 成功
 */
test('Uncollectible invoice: Success', () => {
    prepareConfigs('in_00104', true);

    const invoiceResStr = invoiceRes('uncollectible', 'cus_00004', 'pi_00004', true);
    let reqCount = 0;
    httpClient.setRequestHandler((request) => {
        if (reqCount === 0) {
            assertGetInvoiceRequest(request, 'in_00104');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', invoiceResStr);
        }
        if (reqCount === 1) {
            assertGetPaymentIntentRequest(request, 'pi_00004');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_PAYMENT_INTENT_OK));
        }
        if (reqCount === 2) {
            assertGetCustomerRequest(request, 'cus_00004');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', JSON.stringify(SAMPLE_CUSTOMER_2));
        }
        if (reqCount === 3) {
            assertUpdatePaymentIntentRequest(request, 'pi_00004', 'cus2@example.com');
            reqCount++;
            return httpClient.createHttpResponse(200, 'application/json', '{}');
        }
        assertSendInvoiceRequest(request, 'in_00104');
        return httpClient.createHttpResponse(200, 'application/json', '{}');
    });

    execute();
});
